Scripts.OnEnter:
if ( not this.tooltip ) then
	return;
end
GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
GameTooltip:SetText(this.tooltip);

Scripts.OnTabPressed:
if ( IsShiftKeyDown() ) then
	SendMailSubjectEditBox:SetFocus();
else
	SendMailMoneyGold:SetFocus();
end

Scripts.OnMouseUp:
SendMailBodyEditBox:SetFocus();
if ( CursorHasItem() ) then
	SendMailPackageButton_OnClick();
end

Scripts.OnTabPressed:
if ( IsShiftKeyDown() ) then
	SendMailNameEditBox:SetFocus();
else
	SendMailBodyEditBox:SetFocus();
end

Scripts.OnEnter:
GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
if ( GetSendMailItem() ) then
	GameTooltip:SetSendMailItem();
else
	GameTooltip:SetText(ATTACHMENT_TEXT, 1.0, 1.0, 1.0);
end

Scripts.OnHide:
CloseMail();
HideUIPanel(OpenMailFrame);
StationeryPopupFrame:Hide();
SendMailBodyEditBox:SetText("");
SendMailNameEditBox:SetText("");
SendMailSubjectEditBox:SetText("");
PlaySound("igCharacterInfoClose");

Scripts.OnClick:
if ( OpenMailFrame.cod and (OpenMailFrame.cod > GetMoney()) ) then
	StaticPopup_Show("COD_ALERT");
elseif ( OpenMailFrame.cod ) then
	StaticPopup_Show("COD_CONFIRMATION");
else
	TakeInboxItem(InboxFrame.openMailID);
end
PlaySound("igMainMenuOptionCheckBoxOn");

Scripts.OnEvent:
--GameTooltip:Hide();
if ( GameTooltip:IsOwned(this) ) then
	if ( OpenMailFrame.money ) then
		GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
		SetTooltipMoney(GameTooltip, OpenMailFrame.money);
		GameTooltip:Show();
	end
end

Scripts.OnEnter:
if ( OpenMailFrame.money ) then
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	SetTooltipMoney(GameTooltip, OpenMailFrame.money);
	GameTooltip:Show();
end
