Scripts.OnClick:
if ( this:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end
SHOW_BUFF_DURATIONS = tostring(this:GetChecked());
BuffButtons_UpdatePositions();

Scripts.OnEvent:
if ( event == "VARIABLES_LOADED" ) then
	UIOptionsFrameCheckButtons["SHOW_PARTY_BACKGROUND_TEXT"].value = SHOW_PARTY_BACKGROUND;
	this:SetChecked(SHOW_PARTY_BACKGROUND);
	UpdatePartyMemberBackground();
end

Scripts.OnClick:
PlayClickSound();
STATE_MULTI_ACTIONBAR_1 =  SHOW_MULTI_ACTIONBAR_1;
SHOW_MULTI_ACTIONBAR_1 = this:GetChecked();
MultiActionBar_Update();
UIParent_ManageFramePositions();

PlayClickSound();
STATE_AlwaysShowMultiBars = ALWAYS_SHOW_MULTIBARS;
ALWAYS_SHOW_MULTIBARS = this:GetChecked();
MultiActionBar_UpdateGridVisibility();

PlayClickSound();
SHOW_MULTI_ACTIONBAR_3 = this:GetChecked();
MultiActionBar_Update();
UIParent_ManageFramePositions();
UIOptionsFrame_UpdateDependencies();

PlaySound("gsTitleOptionExit");
HideUIPanel(UIOptionsFrame);
UIOptionsFrameMultiBar_Reset();
MultiActionBar_Update();
ALWAYS_SHOW_MULTIBARS = STATE_AlwaysShowMultiBars;
MultiActionBar_UpdateGridVisibility();
UIParent_ManageFramePositions();

Scripts.OnShow:
SetupFullscreenScale(this);
UIOptionsFrame_Load();
MultiActionBar_Update();
MultiActionBar_ShowAllGrids();
Disable_BagButtons();
UpdateMicroButtons();
